import React,{useState} from 'react'
import PropTypes from 'prop-types'


export default function TextForm(props) {
    const handleOnClick= () => {
        console.log("Uppercase was Clicked" +text);
        let newText = text.toUpperCase();
        setText(newText);
        
    }
    const handleOnChange = (event) => {
        setText(event.target.value);
    }
    const [text, setText] = useState('Enter Text Here');
  return (
    <>
    <div>
      <h1>{props.heading}</h1>
      <form>
  <div className="mb-3">
    <input type="text" className="form-control" value={text} onChange={handleOnChange} aria-describedby="emailHelp"/>
   
  </div>


  
</form>
    <button type="Submit" className='btn btn-primary' onClick={handleOnClick}>Convert To UpperCase</button>
    </div>

    </>
  )
}

TextForm.propTypes={
    heading: PropTypes.string.isRequired
    
}
